mapper.select:
select owner.owner_uid,owner_name,owner_sex,owner_id,owner_phone,owner.community_id house_id,house_number,parking_id,parking_number from owner left join house on house.owner_uid=owner.owner_uid left join parking on parking.owner_uid=owner.owner_uid
and owner_name like "%"#{ownerName}"%"
and owner_phone = #{ownerPhone}
and owner.community_id = #{communityId}
